import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [jsonData, setJsonData] = useState('');

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('image', selectedFile);

    axios.post('http://localhost:5000/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then(response => {
        setExtractedText(response.data.text);

        // Assuming response.data.text is a valid JSON string
        const parsedJson = JSON.parse(response.data.text);
        setJsonData(JSON.stringify(parsedJson, null, 2));
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container mt-5">
      <h1 className="display-4 mb-4">Vision API OCR</h1>
      <div className="mb-3">
        <input type="file" className="form-control" accept="image/*" onChange={handleFileChange} />
      </div>
      <button className="btn btn-primary" type="button" onClick={handleUpload}>
        Upload and Process
      </button>
      <div className="mt-4">
        <h2>Extracted Text:</h2>
        <pre>{extractedText}</pre>
        <h2>JSON Output:</h2>
        <pre>{jsonData}</pre>
      </div>
    </div>
  );
}

export default App;
