import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [jsonData, setJsonData] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('image', selectedFile);

    axios.post('http://localhost:5000/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then(response => {
        setExtractedText(response.data.text);
        const lines = response.data.text.split('\n');
        const parsedData = parseExtractedText(lines);
        setJsonData(parsedData);
      })
      .catch(error => console.error(error));
  };

  const parseExtractedText = (lines) => {
    const jsonData = {
      identification_number: extractValue(lines, /\b(\d{1,4}\s*){5,}\d{1,4}\b/),
      name: extractValue(lines, /Name\s*\n([A-Za-z]+\s[A-Za-z]+)/),
      last_name: extractValue(lines, /[A-Za-z]+\s?[A-Za-z]+/),
      date_of_birth: extractValue(lines, /\d{1,2}\s[A-Za-z]+\s\d{4}/),
      date_of_issue: extractValue(lines, /\d{1,2}\s[A-Za-z]+\s\d{4}/),
      date_of_expiry: extractValue(lines, /\d{1,2}\s[A-Za-z]+\s\d{4}/),
    };

    return jsonData;
  };

  const extractValue = (lines, regex) => {
    let lineIndex = lines.findIndex((line) => regex.test(line));
    if (lineIndex !== -1) {
      // If "Name" is found, check the next line
      const nextLine = lines[lineIndex + 1];
      return nextLine ? nextLine.trim() : "";
    }
    return "";
  };


  return (
    <div>
      <h1>Vision API OCR</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload and Process</button>
      <div>
        <h2>Extracted Text:</h2>
        <pre>{extractedText}</pre>
        <h2>JSON Data:</h2>
        <pre>{JSON.stringify(jsonData, null, 2)}</pre>
      </div>
    </div>
  );
};

export default App;
